mapper.select:
SELECT U.UNIVERSE_ID, U.MEMBER_ID, M.NICKNAME, U.TITLE, U.THUMBNAIL_URL, U.LIKE_COUNT, U.CREATE_DATE FROM TB_UNIVERSE U JOIN TB_MEMBER M ON U.MEMBER_ID = M.MEMBER_ID WHERE U.STATUS = 'Y'
AND ( U.LIKE_COUNT < #{lastLikeCount} OR (U.LIKE_COUNT = #{lastLikeCount} AND U.UNIVERSE_ID < #{lastUniverseId}) )
AND U.UNIVERSE_ID < #{lastUniverseId}
ORDER BY U.LIKE_COUNT DESC, U.UNIVERSE_ID DESC
ORDER BY U.UNIVERSE_ID DESC
LIMIT #{limit}

SELECT * FROM TB_UNIVERSE 
 WHERE UNIVERSE_ID = #{universeId}
   AND STATUS = 'Y'

mapper.insert:
INSERT INTO TB_UNIVERSE (
    TITLE, LAYOUT_DATA, THEME_CODE, MEMBER_ID, THUMBNAIL_URL, STATUS, LIKE_COUNT, CREATE_DATE
) VALUES (
    #{title}, #{layoutData}, #{themeCode}, #{memberId}, #{thumbnailUrl}, 'Y', 0, NOW()
)

mapper.update:
UPDATE TB_UNIVERSE
   SET STATUS = 'N'
 WHERE UNIVERSE_ID = #{universeId}

mapper.select:
SELECT U.UNIVERSE_ID, U.MEMBER_ID, M.NICKNAME, U.TITLE, U.THUMBNAIL_URL, U.LIKE_COUNT, U.CREATE_DATE FROM TB_UNIVERSE U JOIN TB_MEMBER M ON U.MEMBER_ID = M.MEMBER_ID JOIN TB_UNIVERSE_LIKED L ON U.UNIVERSE_ID = L.UNIVERSE_ID WHERE L.MEMBER_ID = #{memberId} AND U.STATUS = 'Y'
AND U.UNIVERSE_ID < #{lastUniverseId}
ORDER BY U.UNIVERSE_ID DESC FETCH NEXT #{limit} ROWS ONLY
